import { clsx, type ClassValue } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

/**
 * Generate a simple blur placeholder data URL for Next.js Image component
 * This is a minimal 1x1 pixel base64-encoded image with dark background
 * Used for placeholder="blur" prop to prevent layout shift
 */
export function getBlurDataURL(): string {
  // Minimal 1x1 pixel base64-encoded image (dark gray/black)
  return 'data:image/jpeg;base64,'
}